CREATE TABLE IF NOT EXISTS t1 (
  a TEXT NOT NULL,
  b TEXT NOT NULL,
  c BYTEA,
  d TIMESTAMP WITH TIME ZONE NOT NULL,
  e BOOLEAN NOT NULL,
  f TEXT,
  g TIMESTAMP WITH TIME ZONE,
  h TIMESTAMP WITH TIME ZONE,
  i INT,
  j  TIMESTAMP WITH TIME ZONE,
  k BIGINT NOT NULL,
  PRIMARY KEY (a, b),
  FAMILY f1 (a, b, c, d, e, f, g),
  FAMILY f2 (h, i, k),
  FAMILY f3 (j)

);
