CREATE TABLE IF NOT EXISTS t1 (
    a UUID NOT NULL,
    b TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP,
    c INT AS (CAST(((extract(epoch from b) ) / 10) AS INT) % 8640) STORED,
    d INT4 NOT NULL DEFAULT CAST(random() * 2147483647 AS INT4),

    CONSTRAINT c1 PRIMARY KEY(a, b, d)
);
