CREATE TABLE IF NOT EXISTS t1 (
    a UUID NOT NULL, 
    b UUID NOT NULL, 
    c VARCHAR(35) NOT NULL,
    d VARCHAR(15) NOT NULL, 
    e INT NULL, 
    f VARCHAR(255) NOT NULL,
    g UUID NOT NULL,
    h UUID NOT NULL,
    i UUID NULL,
    j TIMESTAMP NOT NULL, 
    k TIMESTAMP NOT NULL, 
    l DATE NOT NULL, 
    m VARCHAR(10) NOT NULL, 
    n DATE NULL, 
    o INTEGER NULL, 
    p JSONB NOT NULL,
    q JSONB NOT NULL,
    r JSONB NOT NULL,
    s JSONB NOT NULL,
    t JSONB NOT NULL,
    u JSONB NOT NULL,

    CONSTRAINT c1 PRIMARY KEY ( a ),
    CONSTRAINT c2 UNIQUE ( b, c, d ) ,
    CONSTRAINT c3 UNIQUE ( message_id ) ,
    CONSTRAINT c4 CHECK (m in ('HAPPY', 'GRUMPY','SLEEPY','SNEEZY', 'DOPEY', 'BASHFUL', 'DOC','SHAKY')) ,
    CONSTRAINT c5 CHECK (c in ('EENIE','MEENIE','MINIE','MOH') ),
    CONSTRAINT c6 CHECK (n >= now()::DATE),
    CONSTRAINT c7 CHECK(
    	(n IS NOT NULL AND o IS NULL) OR
	    (n IS NULL AND o IS NOT NULL))
);
